App.WeRolePermissionCheckboxComponent = Ember.Component.extend({
  tagName: 'td',
  role: null,
  permission: null,

  started: false,

  isSaving: false,

  willInsertElement: function() {
    this._super();

    var roleName = this.get('role.name');
    var permissionRoles = this.get('permission.roles').toArray();
    var has = false;

    for (var i = permissionRoles.length - 1; i >= 0; i--) {
      if ( Ember.get(permissionRoles[i], 'name' ) === roleName) {
        has = true;
        break;
      }
    };

    this.set('hasPermission', has);

    this.set('started', true);
  },

  onChangePermission: function() {
    if(!this.get('started')) return;
    this.send('changePermission');
  }.observes('hasPermission'),

  actions: {
    changePermission: function() {
      var flag = this.get('hasPermission');
      var role = this.get('role');
      var permission = this.get('permission');
      var self = this;
      this.set('isSaving', true);

      if (flag) {
        // add
        permission.get('roles').pushObject(role);
      } else {
        // remove
        permission.get('roles').removeObject(role);

      }

      permission.save()
      .then(function() {
        self.set('isSaving', false);
      })

    }
  }
});

(function($, we, Ember, App, async){
  App.WeSelect2Component = Ember.Component.extend({
    tagName: 'input',
    type: 'text',
    classNames: ['select2-element','form-control','input-md','tm-input','tm-input-success'],
    placeholder: 'Share with ...',
    shareWithOptions: [],
    chageEventName: 'onChangeSelect2Data',
    // component events
    didInsertElement:function(){
      var element = this.$();
      var _this = this;

      if(!element.select2){
        return console.error('jquery.select2 Not found on element', element);
      }

      element.select2({
        placeholder: this.get('placeholder'),
        minimumInputLength: 3,
        multiple: true,
        data: this.get('shareWithOptions'),
        formatResult: function(item){
          return item.text;
        },
        formatSelection: function(item){
          return item.text;
        },
        formatSelectionCssClass: function (item) {
          switch(item.model) {
            case 'user':
              return 'model-user';
            case 'group':
              return 'model-group';
          }
          return '';
        },
        dropdownCssClass: 'sharebox-dropdown',
        escapeMarkup: function (m) { return m; }
      });

      element.on('change', function(e) {
        _this.sendAction('chageEventName', e);
      });

      // fetch and set preselected values
      getSelectedItemsFromSharedWith(this.get('shareWithUsers'), this.get('shareInGroups'), function(selectedItems){
        element.select2('data', selectedItems);
      });
    },
    willDestroyElement: function(){
      this.$().select2('destroy');
    },

    // TODO how to call this functions from outside?
    getSelectedItems: function(){
      return this.$().select2('data');
    },
    empty: function(){
      this.$().select2('data', null);
    }
  });

  /**
   *  Format shared with fields to use in share with mecanism
   */
  function getSelectedItemsFromSharedWith(shareWithUsers, shareInGroups, callback){

      if(!shareWithUsers && !shareInGroups){
        return callback([]);
      }

      if(!shareWithUsers){
        shareWithUsers = [];
      }

      if(!shareInGroups){
        shareInGroups = [];
      }

      var selectedItems = [];

      async.each(shareWithUsers, function(user, nextUser){
        console.warn('shareWithUser',user);
        selectedItems.push({
          id: user.id,
          text: user.username,
          model: 'user'
        });
        nextUser();
      },function(){
        async.each(shareInGroups, function(group, nextGroup){
          selectedItems.push({
            id: group.id,
            text: group.name,
            model: 'group'
          });
          nextGroup();
        },function(){
          callback(selectedItems);
        });
      });
  }

})(jQuery, we, Ember, App, async);


App.WeSocketIoComponent = Ember.Component.extend({
  tagName: 'div',
  classNameBindings: ['msgClass'],

  // socket.io to use in this component
  socket: null,
  // reconnecting flag
  isReconecting: false,
  // flag to check if is connected
  isConnected: false,

  tryCount: 1,
  timeToReconnect: 0,

  timeToReconnectInSeconds: function() {
    return this.get('timeToReconnect') / 1000;
  }.property('timeToReconnect'),

  // is disconnected and dont are trying to reconnect
  isDisconnected: function(){
    if ( this.get('isConnected') ) {
      return false;
    }
    return true;
  }.property('isConnected'),

  isConnectedMsg: 'Connected',

  isReconnectingMsg: 'No h conexo.',

  disconectedMsg: 'Desconectado do servidor. ',
  ClickToConnectText: 'Clique aqui para tentar conectar agora',

  msgClass: function() {
    // connected
    if (this.get('isConnected')) {
      // sr-only makes connectd invisible in twitter bootstrap themes
      return 'alert alert-success text-center sr-only';
    }

    // reconnecting
    if ( this.get('isReconecting') ) {
      return 'alert alert-warning text-center';
    }

    return 'alert alert-danger text-center';
  }.property('isReconecting', 'isConnected'),

  msgText: function(){
    // connected
    if (this.get('isConnected')) {
      return this.get('isConnectedMsg');
    }

    // reconnecting
    if ( this.get('isReconecting') ) {
      return this.get('isReconnectingMsg');
    }

    return this.get('disconectedMsg');

  }.property('isReconecting', 'isConnected'),

  init: function() {
    this._super();
    var self = this;
    var socket = this.get('socket');

    // if dont have set a socket.io then get it from global
    if (!socket) {
      if( !window.io.socket ) {
        return Ember.Logger.error('Socket.io not found');
      }
      self.set('socket', window.io.socket);
      // set local socket var
      socket = self.get('socket');
    }

    if( socket.socket && socket.socket.connected ) {
      self.set('isConnected', true);
    }

    socket.on('connect', function () {
      self.set('isConnected', true);
      self.set('isReconecting', false);
    });

    socket.on('disconnect', function () {
      self.set('isConnected', false);
      self.set('isReconecting', false);
    });

    socket.on('connecting', function () {
      console.warn('connecting');
    });

    socket.on('connect_failed', function () {
      self.set('isConnected', false);
    });

    socket.on('reconnect_failed', function () {
      self.set('isConnected', false);
      self.set('isReconecting', false);
    });

    socket.on('close', function () {
      console.warn('close');
    });

    socket.on('reconnect', function () {
      console.warn('reconnect');
    });

    socket.on('reconnecting', function (time, tryCount) {
     self.set('timeToReconnect', time);
     self.set('tryCount', tryCount);
     self.set('isReconecting', true);
    });
  },

  actions: {
    connect: function() {
      this.get('socket').socket.connect();
    },

    // opcional disconnect action
    disconnect: function() {
      this.get('socket').disconnect();
    }
  }
});
